INSERT INTO `db_book` VALUES ('585', '名言佳句辞典', '6', '1661902952905_249336.jpg', '刘振远', '商务印书馆国际有限公司', '35', '9787517601258', '《名言佳句辞典》选收古今中外流传广、影响大的名言佳句11200条，将这些条目按内容分为生活、爱情、社会、奋斗、感情、幸福、智慧、文化、历史、文学、艺术等111类。条目排列有序，便于查检。对一些生僻字、古汉语难解字词给出了注音和注释。通过阅读本书，可提高读者的语言应用和文学素养，增强语言交际和写作能力，适合广大读者，特别是青少年朋友阅读使用。', '30421487', '2022-08-30 23:18:46');
