INSERT INTO `db_book` VALUES ('955', '花季·雨季', '10', '1661906079706_464652.jpg', '郁秀', '晨光出版社', '30.00', '9787541464621', '《花季·雨季》是郁秀的代表作，也是校园青春文学的开山之作。小说描写了特区中学生斑斓多彩的生活，同时对社会生活进行多视角思索，用少年的意识思考着师长，并以轻盈的笔触加以勾勒和描画，字里行间流泄出理想的光彩，呈现出青少年阳光沐浴下的绚丽世界。小说中写的是一群在改革开放前沿的青少年，他们虽然与我们同处一个时代，却极鲜明地表现出这一代人所特有的个性特征。', '26706937', '2022-08-31 00:39:03');
